trans-unit.source:
(function () {
  var articleId = fyre.conv.load.makeArticleId(null);
  fyre.conv.load({}, [{
    el: 'livefyre-comments',
    network: "livefyre.com",
    siteId: "377988",
    articleId: articleId,
    signed: false,
    collectionMeta: {
      articleId: articleId,
      url: fyre.conv.load.makeCollectionUrl(),
    }
  }], function() {});
}());

